import React from 'react';


const Recipe = ({title,calrories,image,ingredients})=>{
    return(
        <div>
            <h1 className="title">{title}</h1>
            <ol>
                {ingredients.map(ingredients =>(
                    <li>{ingredients.text}</li>
                ))}
            </ol>
            <p>{calrories}</p>
            <img src={image}/>
        </div>
    )
}

export default Recipe;
